#include <iostream>
#include <vector>
using namespace std;

vector<int> a(200000, 0);
vector<int> help(200000, 0);
long long cnt = 0;


void MergeSort(int l, int r) {

  int mid = (l + r) / 2;

  if (r - l <= 1) return;
  MergeSort(l, mid);
  MergeSort(mid, r);

  int i = l, j = mid;
  int k = l;
  while(i < mid && j < r) {
    if (a[i] > a[j]) {
      cnt += mid - i ;
      help[k++] = a[j++];
    } else {
      help[k++] = a[i++];
    }
  }
  while(i < mid) help[k++] = a[i++];
  while(j < r) help[k++] = a[j++];

  for (int i = l; i < r; ++i) a[i] = help[i];

}



int main() {
  int n;
  cin >> n;
  for (int i = 0; i < 2*n; ++i) cin >> a[i];
  MergeSort(0, 2*n);
  cout << cnt;

}








