#include<bits/stdc++.h>
using namespace std;

int solve(int s, string str){
	int i = str.length()-1;
	int counter = 0;
	int changes = s;
	while(i>=0){
		if(str[i]=='?'){
			if(counter==0 && changes>0){
				str[i] = '1';
				counter = 1;
				changes--;
			}
			else if(counter==1 && changes>0){
				str[i] = '0';
				counter = 0;
				changes--;
			}
			else if(counter==2 && changes>0){
				str[i] = '0';
				counter = 0;
				changes--;
			}
			else{
				str[i] = '1';
				counter = 1;
			}
		}
		i--;
	}

	if(counter==2){
		return changes+1;
	}
	return changes;
}

